import React, {useEffect} from 'react'
import './Pagination.css'

const Pagination = ({page, setPage}) => {

  const handlePreviousClick = () => {
    if(page===1){
      setPage(1)
    } else{
      setPage(page-1)
      window.scrollTo(0, 0);
    }
  }

  const handleNextClick = () => {
    setPage(page+1)
    window.scrollTo(0, 0);
  }

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [page]);

  return (
    <div className="pagination">
    <button className="button" data-testid="prevButton" onClick={handlePreviousClick}><img src="data:image/png;base64," className="arrow"/></button>
    <button className="button" >{page}</button>
    <button className="button" data-testid="nextButton" onClick={handleNextClick}><img src="data:image/png;base64," className="arrow"/></button>
    </div>
  )
}

export default Pagination
